	    import oracle.odi.mapping.generation.AbstractSyntaxTree.ASTProducerMethod;
		import oracle.odi.domain.mapping.generator.KMGeneratorDelegateRegistry.GeneratorDelegateClass;  	
		@GeneratorDelegateClass(componentType="AP", descriptionKey="GeneratorDelegateClass.LKMHBaseToSql_SQOOP")
        public class LKM_HBase_to_SQL_SQOOP extends LKM_Hive_to_SQL_SQOOP {

            // class constructor; initialization is done in setBinding method
            public LKM_HBase_to_SQL_SQOOP() {}

            // Declare KM level vars
            protected srcStgTableAST

            public void setBinding( Binding binding ) {
                super.setBinding( binding )
                // Initialize variables
            }

            void updateBinding() {
                super.updateBinding()
                // Publish variables into binding to make them available in templates and variableDefText
                mBinding.setVariable( "srcStgTableAST", srcStgTableAST )                     
            }

			@ASTProducerMethod(processingType=ProcessingType.TARGET, producedASTType=HiveSqoopInsert.class, styleKey="ProcessingStyleKey.SQL.INSERT")
            public AbstractSyntaxTree INSERT( SqlQuery input ) {
                //def ast = super.INSERT( input )                                                           
                // Publish variables into binding to make them available in templates and variableDefText

                // Find source DataStore
                def srcLst = input.getFromList().collect { it.getSourceTables().collect { it } }.flatten()
                if ( srcLst.size() != 1 )
                    throw new RuntimeException( "Input query must have exactly one source datastore." )
                def sourceRef = srcLst[0]
                def sourceDataStore = sourceRef.getModelTable().getBoundDataStore()
                def hBaseTableName  = sourceDataStore.getResourceName()

                // Create DDL for HBase-Hive table
                def srcStagingTable = getStagingTable()
                String hiveStagingSchemaName = getOption( "HIVE_STAGING_LSCHEMA" )
                srcStagingTable.setCustomText( '<?=odiRef.getObjectName("L", "' + "%COL_PRF" + physicalNode.getCollTableUniquenessDigits() + physicalNode.getStageTableName() + '", "' + hiveStagingSchemaName + '", "W")?>' )
                srcStgTableAST = getHiveHBaseSerDeTable( srcStagingTable, hBaseTableName, physicalNode )

                // Creat Query AST for select from HBase-Hive table
                // needed for later Hive unload
                SqlQuery hiveHbaseQuery = new SqlQuery( physicalNode.getTemplateUtils() )
                //hiveHbaseQuery.setUseSubselectForMultiJoin(true);
                def tableName = physicalNode.getBoundObjectName();  // The name of the associated datastore object
                def tableQualifier = physicalNode.getLocation() == null ? null : physicalNode.getLocation().getName();
                def tableAlias = generatorContext.generateUniqueAlias(physicalNode);

                FromClause from = new FromClause(new TableReference(srcStagingTable), tableAlias);
                List<FromClause> fromList = new ArrayList<FromClause>();
                fromList.add(from);
                def selectExprList = physicalNode.getPhysicalColumns().collect { col -> new StringExpression( col.getSQLAccessName(tableAlias), col, col.getRefObject() ) }     
                hiveHbaseQuery.setSelectList( selectExprList );
                hiveHbaseQuery.setFromList(fromList);              

                // These can be added as a predefined task, and use TaskControl.setSourceLogicalSchema() to dynamically set the schema.
//                addHivePrepareSessionTask( hiveStagingSchemaName )
//                addHiveSourceTask( "LKM HBase to SQL SQOOP", "Drop Hive-HBase table",   """DROP TABLE IF EXISTS ${srcStgTableAST.tableName()}""", 260, "MAP_BEGIN", hiveStagingSchemaName )
//                addHiveSourceTask( "LKM HBase to SQL SQOOP", "Create Hive-HBase table", """CREATE ${srcStgTableAST.external()} TABLE ${srcStgTableAST.ifNotExists()} ${srcStgTableAST.tableName()}
//(
//	${srcStgTableAST.colListDdl()}
//)
//${srcStgTableAST.partitionedClause()}
//${srcStgTableAST.clusteredClause()}
//${srcStgTableAST.sortByClause()}
//${srcStgTableAST.intoBucketsClause()}
//${srcStgTableAST.rowFormatClause()}""", 270, "MAP_BEGIN", hiveStagingSchemaName )
//                addHiveUnloadTask( hiveHbaseQuery, hiveStagingSchemaName )
                mBinding.setVariable( "HiveUnloadQuery", hiveHbaseQuery )  
//                addHiveSourceTask( "LKM HBase to SQL SQOOP", "Cleanup Hive-HBase table", """DROP TABLE ${srcStgTableAST.tableName()}""", 800, "MAP_CLEANUP", hiveStagingSchemaName )

                // Create DDL for staging table
                defineSqlStagingTable()

                updateBinding()
                HiveSqoopInsert result = new HiveSqoopInsert();
                result.setRawSourceDir(mBinding.getVariable("unquotedSourceDir"));
                result.setHiveUnloadQuery(hiveHbaseQuery)
                return result
            } 

        } // End of class definition

