import oracle.odi.domain.model.OdiDataStore
import oracle.odi.domain.mapping.exception.MappingException
import oracle.odi.km.exception.OdiKMException

public class LKM_SQL_to_Object_Storage_Direct extends LKM_SQL_to_Object_Storage {

        private String xmlCharSet;
        private String storeAsXML;
        protected boolean addCompression;
        protected String compressDirectory;
        protected List<String> filesToCompress;
        protected List<String> compressedList;
        protected String compressionType;
        protected String keepSources;
        protected boolean keep;

        public void setBinding(Binding binding) {

            super.setBinding(binding);
            mBinding = binding;

            storeAsXML = getOptionValue("STORE_AS_XML");
            binding.setVariable("storeAsXML", storeAsXML);

            xmlCharSet = getOptionValue("XML_CHARSET_ENCODING");
            binding.setVariable("xmlCharSet", xmlCharSet);

            if (storeAsXML == 'true') {
               tgtFormat = "XML";
               binding.setVariable("tgtFormat", tgtFormat);
            }

            overwrite = getOptionValue("OVERWRITE_TARGET_FILE");
            binding.setVariable("overwrite", overwrite);

            targetSchema = getTargetSchemaName();
            binding.setVariable("targetSchema", targetSchema);

            binding.setVariable("isObjectStorageStaging", false);

            addCompression = (getOptionValue("ADD_COMPRESSION") == 'true')?true:false;
            binding.setVariable("addCompression", addCompression);

            if (addCompression) {
              def sourceDirectory = tempDirectory;

              compressionType = getOptionValue("COMPRESSION_TYPE");
              binding.setVariable("compressionType", compressionType);

              keepSources = getOptionValue("KEEP_SOURCE_FILES");
              binding.setVariable("keepSources", keepSources);

              keep = (keepSources == 'true')?true:false;
              binding.setVariable("keep", keep);

              compressDirectory = sourceDirectory;
              binding.setVariable("compressDirectory", compressDirectory);

              filesToCompress = [tempFile];
              binding.setVariable("filesToCompress", filesToCompress);

              def suffix = ".bz2";
              if (compressionType.equals("gzip")) { suffix = ".gz" }

              compressedList = []
              filesToCompress.each {compressedList.add(it + suffix)}

              resourceList = compressedList;
              binding.setVariable("resourceList", resourceList);
            }

        }

    }


