      colMandatory = pColMandatory;
			colNullIfErr=pColNullIfErr;
			if (pDecimalSeparator == null) {
				pDecimalSeparator = ".";
			}
			if (pDecimalSeparator.length() != 1) {
				pDecimalSeparator = ".";
			}
			DecimalFormatSymbols mySymbols = new DecimalFormatSymbols();
