Object.Field:
BEGIN

 FOR FlowTableIndexes IN

   (
     SELECT index_name
           FROM all_indexes
           WHERE table_owner   = '<%=odiRef.getInfo("DEST_WORK_SCHEMA")%>'
           AND table_name    = '<%=odiRef.getObjectShortName("L",odiRef.getTableName("INT_SHORT_NAME"),"W")%>'
   )

 LOOP

           EXECUTE immediate('DROP INDEX <%=odiRef.getInfo("DEST_WORK_SCHEMA")%>.'||FlowTableIndexes.index_name);

 END LOOP;

END;

DECLARE

c_pk_list varchar2(20000) := '';
c_pk_name varchar2(30) := '';

BEGIN

  c_pk_name := '<%=odiRef.getObjectShortName("L",odiRef.getTableName("INT_SHORT_NAME"),"W")%>';

  IF length(c_pk_name) > 24 THEN
       c_pk_name := SUBSTR(c_pk_name,1,24);
  END IF;

  c_pk_name := c_pk_name||'_PK';

  FOR TableConstraint IN
  (
     SELECT ROWNUM ROW_NUMBER, c2.column_name
     FROM all_CONSTRAINTS C1
     INNER JOIN all_cons_columns C2
     ON (c1.constraint_name=c2.constraint_name
     AND c1.table_name     =c2.table_name
     AND c1.owner          = c2.owner)
     WHERE C1.TABLE_NAME    ='<%=odiRef.getTargetTable("RES_NAME")%>'
     AND c1.owner           = '<%=odiRef.getTargetTable( "SCHEMA" )%>'
     AND C1.CONSTRAINT_TYPE = 'P'
     AND C1.STATUS = 'ENABLED'
     ORDER BY c1.constraint_name, c2.position
 )

LOOP

     IF c_pk_list IS NOT NULL THEN
       c_pk_list := c_pk_list || ',';
     END IF;

     c_pk_list := c_pk_list || '"' ||TableConstraint.COLUMN_NAME || '"';

END LOOP;

IF c_pk_list IS NOT NULL THEN

EXECUTE immediate('alter table <%=odiRef.getTable("L", "INT_NAME","W")%> add constraint '||c_pk_name||' primary key ('||c_pk_list||')');

END IF;

END;

DECLARE
c_fk_name varchar2(30) := '';
BEGIN
c_fk_name := '<%=odiRef.getObjectShortName("L",odiRef.getTableName("INT_SHORT_NAME"),"W")%>';
IF length(c_fk_name) > 24 THEN c_fk_name := SUBSTR(c_fk_name,1,24); END IF;
c_fk_name := c_fk_name||'_FK';
FOR TableConstraint IN ( SELECT ROWNUM ROW_NUMBER, c2.column_name, C4.OWNER FK_OWNER, c4.table_name FK_TABLE, c4.column_name FK_COLUMN FROM all_CONSTRAINTS C1 INNER JOIN all_cons_columns C2 ON (c1.constraint_name=c2.constraint_name AND c1.table_name =c2.table_name AND c1.owner = c2.owner) INNER JOIN all_CONSTRAINTS C3 ON (c3.constraint_name = c1.r_constraint_name AND C1.OWNER = C3.OWNER) INNER JOIN all_CONS_COLUMNS C4 ON (c3.constraint_name =c4.constraint_name AND c3.table_name =c4.table_name AND c3.owner = c4.owner) WHERE C1.TABLE_NAME ='<%=odiRef.getTargetTable("RES_NAME")%>' AND c1.owner = '<%=odiRef.getTargetTable( "SCHEMA" )%>' AND C1.CONSTRAINT_TYPE = 'R' AND C1.STATUS = 'ENABLED' ORDER BY c1.constraint_name, c2.position )
LOOP EXECUTE immediate 'alter table <%=odiRef.getTable("L", "INT_NAME","W")%> add constraint '||c_fk_name||TO_CHAR(TableConstraint.ROW_NUMBER)||' foreign key ("'||TableConstraint.COLUMN_NAME||'") references "'||TableConstraint.FK_OWNER||'"."'||TableConstraint.FK_TABLE||'" ("'||TableConstraint.FK_COLUMN||'")'; END LOOP;
END;

DECLARE

c_uk_list varchar2(20000) := '';
new_constraint_name varchar2(50) := '';
old_constraint_name varchar2(50) := '';
c_count NUMBER := 0;
c_uk_name varchar2(30) := '';

BEGIN

  c_uk_name := '<%=odiRef.getObjectShortName("L",odiRef.getTableName("INT_SHORT_NAME"),"W")%>';

  IF length(c_uk_name) > 24 THEN
       c_uk_name := SUBSTR(c_uk_name,1,24);
  END IF;

  c_uk_name := c_uk_name||'_UK';

  FOR TableConstraint IN
  (
  SELECT c1.CONSTRAINT_NAME,c2.column_name
  FROM all_CONSTRAINTS C1
  INNER JOIN all_cons_columns C2
  ON (c1.constraint_name=c2.constraint_name
  AND c1.table_name     =c2.table_name
  AND c1.owner          = c2.owner)
  WHERE C1.TABLE_NAME    ='<%=odiRef.getTargetTable("RES_NAME")%>'
  AND c1.owner           = '<%=odiRef.getTargetTable( "SCHEMA" )%>'
  AND C1.CONSTRAINT_TYPE = 'U'
  AND C1.STATUS = 'ENABLED'
  ORDER BY c1.constraint_name, c2.position
  )

LOOP

     new_constraint_name := TableConstraint.CONSTRAINT_NAME;

     IF (new_constraint_name <> old_constraint_name) AND (c_uk_list IS NOT NULL) THEN

          c_count := c_count + 1;

          EXECUTE immediate('alter table <%=odiRef.getTable("L", "INT_NAME","W")%> add constraint '||c_uk_name||TO_CHAR( c_count)||' unique('||c_uk_list||')');
          c_uk_list := '';

     END IF;

     IF c_uk_list IS NOT NULL THEN
       c_uk_list := c_uk_list || ',';
     END IF;

     c_uk_list := c_uk_list || '"' ||TableConstraint.COLUMN_NAME || '"';

     old_constraint_name := new_constraint_name;

END LOOP;

   IF c_uk_list IS NOT NULL THEN
      c_count := c_count + 1;
               EXECUTE immediate('alter table <%=odiRef.getTable("L", "INT_NAME","W")%> add constraint '||c_uk_name||TO_CHAR( c_count)||' unique('||c_uk_list||')');
   END IF;

END;

DECLARE

index_list varchar2(20000) := '';
new_index_name varchar2(50) := '';
old_index_name varchar2(50) := '';
c_index_type varchar2(50) := '';
c_local_index_flg varchar2(10) :='';
c_unique_index_flg varchar2(10) :='';
c_count NUMBER := 0;
c_dx_name varchar2(30) := '';

BEGIN

  c_dx_name := '<%=odiRef.getObjectShortName("L",odiRef.getTableName("INT_SHORT_NAME"),"W")%>';

  IF length(c_dx_name) > 24 THEN
       c_dx_name := SUBSTR(c_dx_name,1,24);
  END IF;

  c_dx_name := c_dx_name||'_DX';

  FOR TableIndex IN
  (
 SELECT i.index_name,
  DECODE(i.index_type,'BITMAP','BITMAP') INDEX_TYPE,
  DECODE(i.uniqueness,'UNIQUE','UNIQUE') UNIQUE_INDEX_FLAG,
  DECODE(p.composite,'YES','LOCAL') LOCAL_INDEX_FLAG,
  i.status,
  c.column_name,
  e.column_expression,
  c.column_position
FROM all_indexes i
JOIN all_ind_columns c
ON (i.owner       = c.index_owner
AND i.index_name  = c.index_name
AND i.table_owner = c.table_owner
AND i.table_name  = c.table_name)
LEFT OUTER JOIN all_ind_expressions e
ON (c.index_owner     = e.index_owner
AND c.index_name      = e.index_name
AND c.table_owner     = e.table_owner
AND c.table_name      = e.table_name
AND c.column_position = e.column_position)
LEFT OUTER JOIN all_ind_partitions p
on (p.index_name = i.index_name
AND p.index_owner = i.owner
AND p.partition_name = '<$="<%=odiRef.getOption("Partition Name")%>"$>')
WHERE i.table_owner   ='<%=odiRef.getTargetTable( "SCHEMA" )%>'
AND i.table_name      ='<%=odiRef.getTargetTable("RES_NAME")%>'
AND i.partitioned = 'YES'

AND NOT EXISTS
  (SELECT *
  FROM all_constraints c
  WHERE c.owner     = i.table_owner
  AND c.table_name      = i.table_name
  AND c.index_name      = i.index_name
  AND c.constraint_type IN ('P','U')
  )
AND NOT EXISTS
(
  SELECT * FROM all_part_indexes a
  WHERE a.owner     = i.owner
  AND a.index_name  = i.index_name
  AND a.table_name  = i.table_name
  AND a.locality   != 'LOCAL'
) 
ORDER BY i.index_name,
  c.column_position
  )

LOOP

     new_index_name := TableIndex.INDEX_NAME;


     IF (new_index_name <> old_index_name) AND (index_list IS NOT NULL) THEN

          c_count := c_count + 1;

          EXECUTE immediate('CREATE '||c_index_type||' '||c_unique_index_flg||' INDEX <%=odiRef.getInfo("DEST_WORK_SCHEMA")%>.'||c_dx_name||TO_CHAR(c_count)||' ON <%=odiRef.getTable("L", "INT_NAME","W")%> ('||index_list||') '||c_local_index_flg);
          index_list := '';

     END IF;

     IF index_list IS NOT NULL THEN
       index_list := index_list || ',';
     END IF;

     IF TableIndex.COLUMN_EXPRESSION IS NOT NULL THEN

         index_list := index_list || TableIndex.COLUMN_EXPRESSION;

     ELSE

         index_list := index_list || '"' ||TableIndex.COLUMN_NAME || '"';

     END IF;     

     old_index_name := new_index_name;
     c_index_type := TableIndex.INDEX_TYPE; 
     c_local_index_flg := TableIndex.LOCAL_INDEX_FLAG;
     c_unique_index_flg := TableIndex.UNIQUE_INDEX_FLAG;

END LOOP;

   IF index_list IS NOT NULL THEN

      c_count := c_count + 1;

      EXECUTE immediate('CREATE '||c_index_type||' '||c_unique_index_flg||' INDEX <%=odiRef.getInfo("DEST_WORK_SCHEMA")%>.'||c_dx_name||TO_CHAR(c_count)||' ON <%=odiRef.getTable("L", "INT_NAME","W")%> ('||index_list||') '||c_local_index_flg);

   END IF;

END;

begin
 execute immediate 'LOCK TABLE <%=odiRef.getTargetTable( "SCHEMA" )%>.<%=odiRef.getTargetTable("RES_NAME")%> PARTITION  (<$="<%=odiRef.getOption("Partition Name")%>"$>)  IN SHARE MODE';
end;

begin
   execute immediate 'ALTER TABLE <%=odiRef.getTargetTable( "SCHEMA" )%>.<%=odiRef.getTargetTable("RES_NAME")%> EXCHANGE <$="<%=odiRef.getOption("Exchange Type")%>"$> <$="<%=odiRef.getOption("Partition Name")%>"$> WITH TABLE <%=odiRef.getTable("L", "INT_NAME","W")%> <$="<%=odiRef.getUserExit("Partition Exchange Options")%>"$>';
end;

--SET SERVEROUTPUT ON; DECLARE c_count NUMBER := 0; BEGIN FOR TableLocalCompositeIndex IN (SELECT tp.table_owner, tp.table_name, tp.partition_name, ip.index_name index_name, ip.composite, sp.subpartition_position, sp.subpartition_name, sp.status FROM all_tab_partitions tp JOIN all_ind_partitions ip ON (tp.partition_name = ip.partition_name) JOIN all_indexes dx ON (ip.index_name = dx.index_name AND dx.table_owner = tp.table_owner AND dx.table_name = tp.table_name) JOIN all_ind_subpartitions sp ON (ip.partition_name = sp.partition_name AND ip.index_name = sp.index_name) WHERE tp.table_owner ='<%=odiRef.getTargetTable("SCHEMA")%>' AND tp.table_name ='<%=odiRef.getTargetTable( "RES_NAME" )%>' AND tp.partition_name ='<$="<%=odiRef.getOption("Partition Name")%>"$>' AND ip.composite = 'YES' ORDER BY sp.subpartition_position ) LOOP --dbms_output.put_line('ALTER TABLE <%=odiRef.getTargetTable("SCHEMA")%>.<%=odiRef.getTargetTable( "RES_NAME" )%> MODIFY SUBPARTITION '||TableLocalCompositeIndex.subpartition_name||' REBUILD UNUSABLE LOCAL INDEXES'); EXECUTE IMMEDIATE('ALTER TABLE <%=odiRef.getTargetTable("SCHEMA")%>.<%=odiRef.getTargetTable( "RES_NAME" )%> MODIFY SUBPARTITION '||TableLocalCompositeIndex.subpartition_name||' REBUILD UNUSABLE LOCAL INDEXES'); c_count := c_count + 1; END LOOP;
IF c_count = 0 THEN
FOR TableLocalNonCompositeIndex IN (SELECT tp.table_owner, tp.table_name, tp.partition_name, ip.index_name index_name, ip.composite, ip.status FROM all_tab_partitions tp JOIN all_ind_partitions ip ON (tp.partition_name = ip.partition_name) JOIN all_indexes dx ON (ip.index_name = dx.index_name AND dx.table_owner = tp.table_owner AND dx.table_name = tp.table_name) WHERE tp.table_owner ='<%=odiRef.getTargetTable("SCHEMA")%>' AND tp.table_name ='<%=odiRef.getTargetTable( "RES_NAME" )%>' AND tp.partition_name ='<$="<%=odiRef.getOption("Partition Name")%>"$>' AND ip.composite = 'NO' ) LOOP --dbms_output.put_line('ALTER TABLE <%=odiRef.getTargetTable("SCHEMA")%>.<%=odiRef.getTargetTable( "RES_NAME" )%> MODIFY PARTITION <$="<%=odiRef.getOption("Partition Name")%>"$> REBUILD UNUSABLE LOCAL INDEXES'); EXECUTE IMMEDIATE('ALTER TABLE <%=odiRef.getTargetTable("SCHEMA")%>.<%=odiRef.getTargetTable( "RES_NAME" )%> MODIFY PARTITION <$="<%=odiRef.getOption("Partition Name")%>"$> REBUILD UNUSABLE LOCAL INDEXES'); END LOOP;
END IF; END;

DECLARE -- Validating KM options

unusable_indexes_count	NUMBER(2,0) := 0;

BEGIN

  select count(*) into unusable_indexes_count
  from all_indexes 
  where table_name = '<%=odiRef.getTargetTable("RES_NAME")%>' and owner = '<%=odiRef.getTargetTable( "SCHEMA" )%>'
  and partitioned = 'NO' and status = 'UNUSABLE';

  IF unusable_indexes_count > 0 THEN 
        raise_application_error(-20101, 'UNUSABLE table indexes exist. All indexes must be in USABLE status before enabling constraints.'); 
  END IF;  

  select count(ip.index_name) into unusable_indexes_count
  from all_indexes ai, all_ind_partitions ip
  where ai.table_owner = '<%=odiRef.getTargetTable( "SCHEMA" )%>' and ai.table_name = '<%=odiRef.getTargetTable("RES_NAME")%>'
  and ai.partitioned = 'YES' and ai.index_name = ip.index_name
  and ip.status = 'UNUSABLE';

  IF unusable_indexes_count > 0 THEN 
        raise_application_error(-20101, 'UNUSABLE partitioned indexes exist. All indexes must be in USABLE status before enabling constraints.'); 
  END IF; 

  select count(isp.index_name) into unusable_indexes_count
  from all_indexes ai, all_ind_subpartitions isp
  where ai.table_owner = '<%=odiRef.getTargetTable( "SCHEMA" )%>' and ai.table_name = '<%=odiRef.getTargetTable("RES_NAME")%>'
  and ai.index_name = isp.index_name and isp.status = 'UNUSABLE';

  IF unusable_indexes_count > 0 THEN 
        raise_application_error(-20101, 'UNUSABLE sub-partitioned indexes exist. All indexes must be in USABLE status before enabling constraints.'); 
  END IF; 

  FOR cur IN
  (
    SELECT fk.owner,
    fk.constraint_name ,
    fk.table_name
    FROM all_constraints fk
    WHERE fk.owner             = '<%=odiRef.getTargetTable( "SCHEMA" )%>'
    AND fk.TABLE_NAME        = '<%=odiRef.getTargetTable("RES_NAME")%>'
    AND fk.STATUS = 'DISABLED'
  )
  LOOP
    EXECUTE immediate 'ALTER TABLE <%=odiRef.getTargetTable( "SCHEMA" )%>.<%=odiRef.getTargetTable("RES_NAME")%> ENABLE CONSTRAINT '||cur.constraint_name;
  END LOOP;
END;

--SET SERVEROUTPUT ON;
DECLARE
  c_partition_block VARCHAR2(20000) := '';
  c_count           NUMBER          := 0;
BEGIN
  FOR PartitionColumn IN
  ( SELECT DISTINCT ap.subpartitioning_type,
    sk.column_name,
    sk.column_position
  FROM all_part_tables ap
  JOIN all_tab_subpartitions sp
  ON (ap.owner      = sp.table_owner
  AND ap.table_name = sp.table_name)
  JOIN all_subpart_key_columns sk
  ON (sp.table_owner           = sk.owner
  AND sp.table_name            = sk.name)
  WHERE ap.owner               ='<%=odiRef.getTargetTable( "SCHEMA" )%>'
  AND ap.table_name            ='<%=odiRef.getTargetTable("RES_NAME")%>'
  AND ap.subpartitioning_type IN ('RANGE','LIST','HASH')
  AND sp.partition_name        = '<$="<%=odiRef.getOption("Partition Name")%>"$>'
  ORDER BY sk.column_position
  )
  LOOP
    IF c_partition_block IS NULL THEN
      c_partition_block  := ' PARTITION BY '||PartitionColumn.subpartitioning_type||' ('||PartitionColumn.column_name;
    END IF;
    IF c_count           > 0 THEN
      c_partition_block := c_partition_block||', '||PartitionColumn.column_name;
    END IF;
    c_count := c_count + 1;
  END LOOP;
  IF c_count           > 0 THEN
    c_partition_block := c_partition_block||')';
  END IF;
  c_count            := 0;
  FOR TablePartition IN
  (SELECT sp.subpartition_name partition_name,
    sp.subpartition_position partition_pos,
    ap.subpartitioning_type,
    DECODE(ap.subpartitioning_type,'RANGE',' VALUES LESS THAN ','LIST',' VALUES ') partition_syntax,
    sp.high_value
  FROM all_part_tables ap
  JOIN all_tab_subpartitions sp
  ON (ap.owner                 = sp.table_owner
  AND ap.table_name            = sp.table_name)
  WHERE ap.owner               ='<%=odiRef.getTargetTable( "SCHEMA" )%>'
  AND ap.table_name            ='<%=odiRef.getTargetTable("RES_NAME")%>'
  AND ap.subpartitioning_type IN ('RANGE','LIST','HASH')
  AND sp.partition_name        = '<$="<%=odiRef.getOption("Partition Name")%>"$>'
  ORDER BY sp.subpartition_position
  )
  LOOP
    IF c_count           = 0 THEN
      c_partition_block := c_partition_block||' (  ';
    END IF;
    IF c_count           > 0 THEN
      c_partition_block := c_partition_block||', ';
    END IF;
    c_partition_block                       := c_partition_block||'PARTITION '||TablePartition.partition_name;
    IF (TablePartition.subpartitioning_type IN ('RANGE', 'LIST')) THEN
      c_partition_block                     := c_partition_block||TablePartition.partition_syntax||' ('||TablePartition.high_value||')';
    END IF;
    c_count := c_count + 1;
  END LOOP;
  IF c_partition_block IS NOT NULL THEN
    c_partition_block  := c_partition_block||')';
  END IF;
  --dbms_output.put_line('create table <%=odiRef.getTable("L", "INT_NAME","W")%> (<%=odiRef.getColList("", "[COL_NAME]\t\t[DEST_WRI_DT] NULL", ", ", "", "")%>) '||c_partition_block||'  nologging <$="<%=odiRef.getUserExit("Degree of Parallelism")%>"$>');
       EXECUTE immediate 'create table <%=odiRef.getTable("L", "INT_NAME","W")%> (<%=odiRef.getColList("", "[COL_NAME]\t\t[DEST_WRI_DT] NULL", ", ", "", "")%>) '||c_partition_block||'  nologging <$="<%=odiRef.getUserExit("Degree of Parallelism")%>"$>';
END;

DECLARE

c_ck_name varchar2(30) := '';
c_count NUMBER := 0;


BEGIN

  c_ck_name := '<%=odiRef.getObjectShortName("L",odiRef.getTableName("INT_SHORT_NAME"),"W")%>';

  IF length(c_ck_name) > 24 THEN
       c_ck_name := SUBSTR(c_ck_name,1,24);
  END IF;

  c_ck_name := c_ck_name||'_CK';

  FOR TableConstraint IN
  (
     SELECT search_condition
     FROM all_CONSTRAINTS
     WHERE TABLE_NAME    ='<%=odiRef.getTargetTable("RES_NAME")%>'
     AND OWNER           = '<%=odiRef.getTargetTable( "SCHEMA" )%>'
     AND CONSTRAINT_TYPE = 'C'
     AND STATUS = 'ENABLED'
     AND CONSTRAINT_NAME NOT LIKE  'SYS%'
 )

LOOP

     c_count := c_count + 1;

     EXECUTE immediate('alter table <%=odiRef.getTable("L", "INT_NAME","W")%> add constraint '||c_ck_name||TO_CHAR( c_count)||' check ('||TableConstraint.search_condition||')');

END LOOP;

END;

BEGIN

   FOR TableConstraint IN
  (
     SELECT COLUMN_NAME
     FROM ALL_TAB_COLUMNS
     WHERE TABLE_NAME    ='<%=odiRef.getTargetTable("RES_NAME")%>'
     AND OWNER           = '<%=odiRef.getTargetTable( "SCHEMA" )%>'
     AND NULLABLE = 'N'
 )

LOOP

     EXECUTE immediate('alter table <%=odiRef.getTable("L", "INT_NAME","W")%> modify ("'||TableConstraint.column_name||'" NOT NULL)');

END LOOP;

END;
