//==========================================================
//===================== to HBase ===========================
//==========================================================
if (isToHBase) { 

    // Some validations
    // Is target column key mapped?
    if ( !odiRef.getColList("|", "[COL_NAME]", "|", "|").contains("|key|") ) {
        throw new OdiKMException(
        "ODIKM-HADOOP-IKM-10000", "ODIKM-HADOOP-IKM-10000: Target column key is not mapped.", 
        null );
    }
    // Are all columns part of the same column family?
    hBaseColumnFamily = null;
    cols = odiRef.getColList("", "[COL_NAME]", "|", "").split(""+(char)92+"|");
    i = 0;
    while (i < cols.length) {
        col = cols[i];
        if ( col.contains(":") ) {
            colFam = col.substring(0, col.indexOf(":"));
            if ( hBaseColumnFamily == null) {
                hBaseColumnFamily = colFam;
            } else if ( !hBaseColumnFamily.equals( colFam ) ) {
                throw new OdiKMException(
                "ODIKM-HADOOP-IKM-10000", "ODIKM-HADOOP-IKM-10000: Columns from different column families mapped. This is not supported by SQOOP.", 
                null );
            }
        } else if ( !col.equals("key") ) {
            throw new OdiKMException(
            "ODIKM-HADOOP-IKM-10000", "ODIKM-HADOOP-IKM-10000: Unsupported column name: {0}.", 
            new String[] { col }  );
        }
        i++;
    }
%>

