/* ------ Begin of List of all EBS Tables ----- */ (select table_name, num_rows, owner from all_tables t where exists ( /* There can be more than one synonym per table => EXISTS predicate */ select 1 from all_synonyms syn where syn.owner = 'APPS' and t.owner = syn.table_owner and t.table_name = syn.table_name ) ) t
