/* Find the *optional* entry in FND_TABLES */ left outer join applsys.fnd_tables oat on (oat.table_name = t.table_name) )
/* Lets now associate the found table to its application: */ /* if we have an entry in FND_TABLES, we join by appid otherwise we join by oracle_userid Note: in most cases t.owner = oau.oracle_username, but not always. E.g. for tables in application JE (appid = 7002) AP.JE_IT_EXEMPT_LETTERS */ inner join (( applsys.fnd_oracle_userid oau inner join applsys.fnd_product_installations oap on (oau.oracle_id = oap.oracle_id)) inner join applsys.fnd_application oaa on (oap.application_id = oaa.application_id) ) on ( oat.table_name IS NOT NULL AND (oat.application_id = oap.application_id) OR oat.table_name IS NULL AND (t.owner = oau.oracle_username) )
