import java.sql.Connection;
import java.sql.PreparedStatement;
import java.sql.SQLException;
import com.sunopsis.dwg.snpreference.OdiRefLogger;
import java.util.logging.Level;

public class OdiKmReverse {

	protected static OdiRefLogger logger;

	// ODI 12c DML
	private static final String DML_INSERT_SNP_REV_TABLE_GUID   = "INSERT INTO SNP_REV_TABLE  (MOD_GUID, TABLE_NAME, RES_NAME, TABLE_ALIAS, TABLE_TYPE, TABLE_DESC_C, SMOD_CODE, R_COUNT, IND_SHOW) values (?,?,?,?, 'T', ?, ?, 1, '1')";
	private static final String DML_INSERT_SNP_REV_COLUMN_GUID  = "INSERT INTO SNP_REV_COL    (MOD_GUID, TABLE_NAME, COL_NAME, COL_HEADING, COL_DESC_C, DT_DRIVER, POS, LONGC, SCALEC, BYTES, COL_MANDATORY, CHECK_FLOW, CHECK_STAT) values (?,?,?,?,?,?,?,?,?,?,?,?,?)";
	private static final String DML_INSERT_SNP_REV_KEY_GUID     = "INSERT INTO SNP_REV_KEY    (MOD_GUID, TABLE_NAME, KEY_NAME, CONS_TYPE, IND_ACTIVE, CHECK_FLOW, CHECK_STAT) values (?, ?,?,'PK', '1','1','1')";
	private static final String DML_INSERT_SNP_REV_KEY_COL_GUID = "INSERT INTO SNP_REV_KEY_COL(MOD_GUID, TABLE_NAME, KEY_NAME, COL_NAME, POS) values (?,?,?,?,?)";

	// ODI 11g DML
	private static final String DML_INSERT_SNP_REV_TABLE        = "INSERT INTO SNP_REV_TABLE  (I_MOD, TABLE_NAME, RES_NAME, TABLE_ALIAS, TABLE_TYPE, TABLE_DESC, SMOD_CODE, R_COUNT, IND_SHOW) values (?,?,?,?, 'T', ?, ?, 1, '1')";
	private static final String DML_INSERT_SNP_REV_COLUMN       = "INSERT INTO SNP_REV_COL    (I_MOD,TABLE_NAME,COL_NAME,COL_HEADING,COL_DESC,DT_DRIVER,POS,LONGC,SCALEC,BYTES,COL_MANDATORY,CHECK_FLOW,CHECK_STAT) values (?,?,?,?,?,?,?,?,?,?,?,?,?)";
	private static final String DML_INSERT_SNP_REV_KEY          = "INSERT INTO SNP_REV_KEY    (I_MOD, TABLE_NAME, KEY_NAME, CONS_TYPE, IND_ACTIVE, CHECK_FLOW, CHECK_STAT) values (?, ?,?,'PK', '1','1','1')";
	private static final String DML_INSERT_SNP_REV_KEY_COL      = "INSERT INTO SNP_REV_KEY_COL(I_MOD, TABLE_NAME, KEY_NAME, COL_NAME, POS) values (?,?,?,?,?)";

	boolean guidMode = true;   // Is repo in 12c GUID mode?
	private Connection con;
	private PreparedStatement tablePStmt,colPStmt,keyPStmt,keyColPStmt;
	protected Object model;

	public OdiKmReverse( Connection repoCon, Object model) {
                //throw new RuntimeException( "Hello!");
		this.con      = repoCon;
		this.model    = model; 
		logger = odiRef.getLogger();
		logger.log(Level.INFO, "Logger instantiated.");	
		logger.log(Level.INFO, "Model: " +  model);	

		// set guidMode based on whether the Model id is an old internal id (Integer) or a new GUID (String)
		this.guidMode = (model instanceof String);
	}


	/**
	 * Create Data Store
	 * 
	 * @param tableName
	 * @param tableDesc
	 * @param subModelCode
	 * @throws Exception
	 */
	protected void createTable(String tableName, String aliasName,
							 String tableDesc, 
							 String subModelCode) throws SQLException {

		logger.log(Level.INFO, "Adding ODI DataStore to JDBC Batch: tableName: " + tableName + ", tableAlias: " + aliasName + ", tableDesc: " + tableDesc + ", subModelCode: " + subModelCode);	
		if(tablePStmt == null){
			tablePStmt = con.prepareStatement( guidMode?DML_INSERT_SNP_REV_TABLE_GUID:DML_INSERT_SNP_REV_TABLE );
		}

	    // Set values
	    if ( guidMode ) {
	    	tablePStmt.setString(1, (String)model ); //MOD_GUID
	    } else {
	    	//tablePStmt.setInt(1, (Integer)model); //I_MOD
	    	// BSh does not support Auto-Unboxing
	    	tablePStmt.setInt(1, ((Integer)model).intValue()); //I_MOD
	    }
		tablePStmt.setString(2, tableName); // Table Name
		tablePStmt.setString(3, tableName); // Res Name
		tablePStmt.setString(4, aliasName); // Table Alias
		if ( tableDesc    == null ) { tablePStmt.setNull  (5, java.sql.Types.VARCHAR ); } // Table Description
		else                          tablePStmt.setString(5, tableDesc );                // Table Description
		if ( subModelCode == null ) { tablePStmt.setNull  (6, java.sql.Types.VARCHAR ); } // Code of the Sub Model it belongs to
		else                          tablePStmt.setString(6, subModelCode );             // Code of the Sub Model it belongs to
		tablePStmt.addBatch();
		logger.log(Level.INFO, "Added DataStore successfully");
	}


	/**
	 * Create Column
	 * 
	 */
	protected void createColumn(String tableName, String colName, String colHeading,
			String colDesc, String colDataType, int colPos, Integer colLen, Integer decLen,
			String colMan) throws SQLException {

				logger.log(Level.INFO, "Adding ODI Column to JDBC Batch: tableName: " + tableName + ", colName: " + colName + ", colHeading: " + colHeading + ", colDesc: " + colDesc + ", colDataType: " + colDataType + ", colPos: " + colPos + ", colLen: " + colLen + ", decLen: " + decLen + ", colMan: " + colMan);
				if(colPStmt == null){
					colPStmt = con.prepareStatement( guidMode?DML_INSERT_SNP_REV_COLUMN_GUID:DML_INSERT_SNP_REV_COLUMN );
				}

			    // Set values
			    if ( guidMode ) {
			    	colPStmt.setString(1, (String)model ); //MOD_GUID
			    } else {
			    	//colPStmt.setInt(1, (Integer)model); //I_MOD
			    	// BSh does not support Auto-Unboxing
			    	colPStmt.setInt(1, ((Integer)model).intValue()); //I_MOD
			    }
				colPStmt.setString(2, tableName); //Table Name
				colPStmt.setString(3, colName); //Col Name
				if ( colHeading == null ) { colPStmt.setNull  (4, java.sql.Types.VARCHAR ); } //Col Heading
				else                        colPStmt.setString(4, colHeading );               //Col Heading
				if ( colDesc    == null ) { colPStmt.setNull  (5, java.sql.Types.VARCHAR ); } //Col Desc
				else                        colPStmt.setString(5, colDesc );                  //Col Desc
				colPStmt.setString(6, colDataType); // Col Data Type
				colPStmt.setInt(7, colPos); //Col Pos
				if ( colLen == null ) { colPStmt.setNull(8, java.sql.Types.DECIMAL); } //Col Len
				else                    colPStmt.setInt (8, colLen);                   //Col Len
				if ( decLen == null ) { colPStmt.setNull(9, java.sql.Types.DECIMAL); } //SCALEC
				else                    colPStmt.setInt (9, decLen);                   //SCALEC
				colPStmt.setInt(10, 0); //BYTES
				colPStmt.setString(11, colMan);//COL_MANDATORY- "0" or "1"
				colPStmt.setString(12, "0"); //CHECK_FLOW
				colPStmt.setString(13, "0"); //CHECK_STAT
				colPStmt.addBatch();
				logger.log(Level.INFO, "Added column successfully");
			}

	/**
	 * Create PK entry to map for which table this Primary Key will be used
	 */
	protected void createPK(String tableName) throws SQLException {

		logger.log(Level.INFO, "Adding PK Constraint to JDBC Batch: tableName:" + tableName); 
		if(keyPStmt == null){
			keyPStmt = con.prepareStatement( guidMode?DML_INSERT_SNP_REV_KEY_GUID:DML_INSERT_SNP_REV_KEY );
		}

	    // Set values
	    if ( guidMode ) {
	    	keyPStmt.setString(1, (String)model ); //MOD_GUID
	    } else {
	    	//keyPStmt.setInt(1, (Integer)model); //I_MOD
	    	// BSh does not support Auto-Unboxing
	    	keyPStmt.setInt(1, ((Integer)model).intValue()); //I_MOD
	    }
		keyPStmt.setString(2, tableName); //Table Name
		String pkName = "PK_"+ tableName;
		keyPStmt.setString(3, pkName); //Unique Key Name- prefixed with "PK_"
		keyPStmt.addBatch();
		logger.log(Level.INFO, "Added constraint successfully");
	}

	/**
	 * Set details of the primary key
	 */
	protected void createPKCol(String tableName, String colName, int colPos) throws SQLException
			 {
				logger.log(Level.INFO, "Adding PK Column to Batch: tableName: " + tableName + ", colName: " + colName + ", colPos: " + colPos);
				if(keyColPStmt == null){
					keyColPStmt = con.prepareStatement( guidMode?DML_INSERT_SNP_REV_KEY_COL_GUID:DML_INSERT_SNP_REV_KEY_COL );
				}

			    // Set values
			    if ( guidMode ) {
			    	keyColPStmt.setString(1, (String)model ); //MOD_GUID
			    } else {
			    	//keyColPStmt.setInt(1, (Integer)model);    //I_MOD
			    	// BSh does not support Auto-Unboxing
			    	keyColPStmt.setInt(1, ((Integer)model).intValue()); //I_MOD
			    }
				keyColPStmt.setString(2, tableName); //Table Name
				String pkName = "PK_"+ tableName;
				keyColPStmt.setString(3, pkName); //Unique Key Name- prefixed with "PK_"
				keyColPStmt.setString(4, colName);
				keyColPStmt.setInt(5, colPos);
				keyColPStmt.addBatch();
				logger.log(Level.INFO, "Added PK column successfully");
			}

	/**
	 * Commit changes
	 */
	protected void commit() throws SQLException {
		logger.log(Level.INFO, "Executing batches and closing statements...");
		if(tablePStmt != null)  { tablePStmt.executeBatch();  tablePStmt.close();  tablePStmt=null; }
		if(colPStmt != null)    { colPStmt.executeBatch();    colPStmt.close();    colPStmt=null;   }
		if(keyPStmt != null)    { keyPStmt.executeBatch();    keyPStmt.close();    keyPStmt=null;   }
		if(keyColPStmt != null) { keyColPStmt.executeBatch(); keyColPStmt.close(); keyColPStmt=null;}
		logger.log(Level.INFO, "Executing batches and closing statements...Done");
	}
}

import java.io.IOException;
import java.sql.Connection;
import java.sql.DriverManager;
import java.sql.PreparedStatement;
import java.sql.SQLException;
import java.util.HashSet;
import java.util.Map;
import java.util.Map.Entry;
import java.util.regex.Pattern;
import java.util.NavigableMap;
import org.apache.hadoop.conf.Configuration;
import org.apache.hadoop.hbase.HBaseConfiguration;
import org.apache.hadoop.hbase.HColumnDescriptor;
import org.apache.hadoop.hbase.HTableDescriptor;
import org.apache.hadoop.hbase.KeyValue;
import org.apache.hadoop.hbase.MasterNotRunningException;
import org.apache.hadoop.hbase.ZooKeeperConnectionException;
import org.apache.hadoop.hbase.client.HBaseAdmin;
import org.apache.hadoop.hbase.client.HTable;
import org.apache.hadoop.hbase.client.HTableInterface;
import org.apache.hadoop.hbase.client.Result;
import org.apache.hadoop.hbase.client.ResultScanner;
import org.apache.hadoop.hbase.client.Scan;
import org.apache.hadoop.hbase.util.Bytes;

public class OdiHBaseReverse extends OdiKmReverse {

	protected static final String HBASE_CONFIGURATION_ZOOKEEPER_QUORUM = "hbase.zookeeper.quorum";
	protected static final String HBASE_CONFIGURATION_ZOOKEEPER_CLIENTPORT = "hbase.zookeeper.property.clientPort";

	public String sTableNamePattern = "%";
	public String sColumnFamilyNamePattern = "%";
	public String sRevAliasLTrim = "";
	public byte[] startRow = null;
	public byte[] stopRow = null;
	public long maxRows = Long.MAX_VALUE;


	private Configuration hConf;
	private HBaseAdmin hBase;

	public OdiHBaseReverse( Connection repoCon, Object model) throws Exception {
		super( repoCon, model);
	}

	public void reverse() throws SQLException {
		logger.log(Level.INFO, "Starting HBase Reverse for Model: " + model); 
		logger.log(Level.INFO, "Using Table Name Pattern: " + sTableNamePattern + ", ColumnFamilyPattern: " + sColumnFamilyNamePattern); 

		Pattern pTableNamePattern = Pattern.compile(sTableNamePattern.replaceAll("%", ".*").replaceAll("_", "."));
		Pattern pColumnFamilyNamePattern = Pattern.compile(sColumnFamilyNamePattern.replaceAll("%", ".*").replaceAll("_", "."));

		try {
			// List all tables
			logger.log(Level.INFO, "Retrieving list of HBase tables..."); 
			HTableDescriptor[] tables = hBase.listTables( pTableNamePattern );
			logger.log(Level.INFO, "Iterating over tables..."); 
			for ( HTableDescriptor t : tables ) {
				String tableName = t.getNameAsString();
				logger.log(Level.INFO, "Processing HBase table: " + tableName); 

				String aliasName = tableName.startsWith(sRevAliasLTrim)?tableName.substring(sRevAliasLTrim.length()):tableName;
				HTable hTable = new HTable( hConf, tableName );

				// Create ODI DataStore
				createTable( tableName, aliasName, null, null );
				HashSet/*<String>*/ colList = new HashSet/*<String>*/();
				long rowCount = 0;  
				// Create ODI column for HBase key column
				int colPos = 0;
				createColumn( tableName, "key", "key", null, "BINARY", colPos, null, null, "1" );
				colList.add("key");
				colPos++;

				// Define the scan
				Scan scan; 

				// Do we need to restrict the scan to a subset of rows?
				if ( startRow == null && stopRow == null ) {
					scan = new Scan(); 
				} else if ( stopRow == null ) {
					scan = new Scan( startRow ); 
				} else {
					scan = new Scan( startRow, stopRow ); 					
				}

				// Only a single version is needed. Anything else will just take more time 
				scan.setMaxVersions(1);

				// Do we need to restrict the scan to certain ColFamilies?
				if ( !sColumnFamilyNamePattern.equals("%") ) {
					HColumnDescriptor[] colFamilies = t.getColumnFamilies();
					for ( HColumnDescriptor colFamily : colFamilies ) {
						String colFamName = colFamily.getNameAsString();
						if ( pColumnFamilyNamePattern.matcher( colFamName ).matches()) {
							logger.log(Level.INFO, "ColumnFamily added: " + colFamName );
							scan.addFamily( colFamily.getName() );
						} else {
							logger.log(Level.INFO, "ColumnFamily skipped: " + colFamName );
						}
					}
				}

				scan.setBatch(100);  // Read in batches of 100
				ResultScanner rs;
				try {
					rs = hTable.getScanner(scan);
					for (HColumnDescriptor col : hTable.getTableDescriptor().getColumnFamilies() ) {
						String colFamName = col.getNameAsString();
						logger.log(Level.INFO, "Found HBase column family: " + colFamName); 
					}
					for (Result r = rs.next(); r != null && rowCount <= maxRows; r = rs.next()) {
						rowCount++;
//						Remove generics, as this is not supported in BSh				
//						NavigableMap<byte[],NavigableMap<byte[],byte[]>> m = r.getNoVersionMap();
//						for (Entry<byte[], NavigableMap<byte[], byte[]>> f : m.entrySet()) {
//							for (Entry<byte[], byte[]> c : f.getValue().entrySet() ) {
//						String colName    = new String(f.getKey()) + ":" + new String(c.getKey());
						NavigableMap m = r.getNoVersionMap();
						for (Object f : ((NavigableMap) m).entrySet()) {
							for (Object c : ((NavigableMap) ((Entry)f).getValue()).entrySet() ) {
								String colName    = new String((byte[])((Entry) f).getKey()) + ":" + new String((byte[])((Entry) c).getKey());
								if ( !colList.contains(colName) ) {
									logger.log(Level.INFO, "Found HBase column %s. Adding ODI Colum: " + colName);
									createColumn(tableName, colName, colName, null, "BINARY", colPos, null, null, "0");
									colList.add( colName );
									logger.log(Level.INFO, "Column " + colName + " added");
									colPos++;
								}
							}
						}
						/*for (KeyValue kv : r.raw()) {
							// each kv represents a column
							System.out.println( kv.toString() );
						}*/
					}
					rs.close();
				} catch (IOException e) {
					// TODO Auto-generated catch block
					e.printStackTrace();
				}

				commit();
			}
		} catch (IOException e1) {
			// TODO Auto-generated catch block
			e1.printStackTrace();
		}


	}

	public void connect( String quorum ) throws MasterNotRunningException, ZooKeeperConnectionException {
		logger.log(Level.INFO, "Connecting to HBase"); 
		logger.log(Level.INFO, "Using ZookeeperQuorum: " + quorum); 

		hConf = HBaseConfiguration.create();
		hConf.set(HBASE_CONFIGURATION_ZOOKEEPER_QUORUM, quorum);
		//hConf.set(HBASE_CONFIGURATION_ZOOKEEPER_CLIENTPORT, hbaseZookeeperClientPort);
		hConf.set("hbase.client.operation.timeout","500");
		hConf.set("hbase.rpc.timeout","500");

		// Check HBase availability
		HBaseAdmin.checkHBaseAvailable(hConf);
		// Create connection
		hBase = new HBaseAdmin( hConf );

		logger.log(Level.INFO, "Successfully connected to HBase"); 
	}

}
